sys_script_client.script:
function onSubmit() {
	var username = g_form.getValue('u_string_1');
	var password = g_form.getValue('u_string_2');
	var email = g_form.getValue('u_string_3');
	var gender = g_form.getValue('u_choice_4');
	var birthday = g_form.getValue('u_glide_date_5');
	var address = g_form.getValue('u_translated_text_6');

	var message = '';
	if (email == '') {
		message += 'Email must be not empty !\n';
		g_form.getControl('u_string_3').style.border = "1px solid red";
	} else {
		g_form.getControl('u_string_3').style.border = "1px solid #cbcbcb";
	}

	if (gender == '') {
		message += 'Gender must be choose !\n';
		g_form.getControl('u_choice_4').style.border = "1px solid red";
	} else {
		g_form.getControl('u_choice_4').style.border = "1px solid #cbcbcb";
	}

	if (birthday == '') {
		message += 'Birthday must be not empty !\n';
		g_form.getControl('u_glide_date_5').style.border = "1px solid red";
	} else {
		g_form.getControl('u_glide_date_5').style.border = "1px solid #cbcbcb";
	}

	if (address == '') {
		message += 'Address must be not empty !\n';
		g_form.getControl('u_translated_text_6').style.border = "1px solid red";
	} else {
		g_form.getControl('u_translated_text_6').style.border = "1px solid #cbcbcb";
	}

	if (message != '') {
		alert(message);
		return false;
	}
}
